import React from "react";
import {Card, Avatar, List, Input, Button, Divider, message} from "antd";
import {connect} from "react-redux";
import {addNewFollowing, unfollow} from "../actions";
import {CloseCircleOutlined} from "@ant-design/icons";
import {store} from "../index";

// TODO: 1. Virtualization for performance
//       2. Sticky

const Followings = (props) => {

    const addNewFollowing = (userName) => {
        props.addNewFollowing({
            userName: userName,
        });
        let curState = store.getState();
        if (curState.addFollowingMessage) {
            message.success(curState.addFollowingMessage, 1).then();
        }
    }

    const createUnfollow = (userName) => {
        return () => {
            props.unfollow({
                userName: userName,
            });
        }
    }

    return (
        <Card bordered={true}>
            <Divider orientation="left">My followings</Divider>
            <Input.Search
                allowClear
                enterButton="Add Following"
                size="large"
                onSearch={addNewFollowing}
            />
            <List
                dataSource={props.followings}
                renderItem={(item) => (
                    <List.Item actions={[
                        <Button type="text" onClick={createUnfollow(item.userName)}>
                            <CloseCircleOutlined/>
                        </Button>]}>
                        <List.Item.Meta
                            avatar={<Avatar src={item.avatar}/>}
                            title={item.userName}
                            description={item.status}
                        />
                    </List.Item>
                )}
            />
        </Card>
    )
}

const mapStateToProps = (state) => {
    return {
        followings: state.followings
    }
};

const mapDispatchToProps = (dispatch) => {
    return {
        addNewFollowing: (values) => dispatch(addNewFollowing(values)),
        unfollow: (values) => dispatch(unfollow(values))
    }
}

export default connect(mapStateToProps, mapDispatchToProps)(Followings);
