import React, {useEffect, useState} from "react";
import {Button, Card, Form, Input, Comment as AntdComment, message, Tooltip} from "antd";
import {DeleteOutlined, EditOutlined} from "@ant-design/icons";
import {showBackendMessage} from "../util/Functions";
import moment from "moment";

const Comment = (props) => {
    message.config({
        rtl: false,
    });

    const [hideEditor, setHideEditor] = useState(true);
    const [username, setUsername] = useState("");
    const [id, setId] = useState(props.id);
    const [author, setAuthor] = useState("");
    const [authorDisplayedName, setAuthorDisplayedName] = useState("");
    const [avatar, setAvatar] = useState("");
    const [text, setText] = useState("");
    const [timeStamp, setTimeStamp] = useState(0);

    const load = async () => {
        await fetch("http://localhost:4000/comment/" + id, {
            method: "GET",
            credentials: "include"
        }).then(res => {
            if (res.ok) {
                showBackendMessage().then();
                return res.json();
            } else {
                throw res.statusText;
            }
        }).then(async res => {
            setAuthor(res.username);
            setText(res.text);
            setTimeStamp(res.timestamp);
            await fetch("http://localhost:4000/profile/" + author, {
                method: "GET",
                credentials: "include",
            }).then(res => {
                if (res.ok) {
                    showBackendMessage().then();
                    return res.json();
                } else {
                    throw res.statusText;
                }
            }).then(res => {
                setAuthorDisplayedName(res.displayed_name);
                setAvatar(res.avatar);
            });
        }).catch(err => message.error(err, 1));
    }

    const onDelete = async () => {
        await fetch("http://localhost:4000/comment/" + id, {
            method: "DELETE",
            credentials: "include",
            headers: {
                "Content-Type": "application/json",
            },
            body: JSON.stringify({
                articleId: props.articleId,
            })
        }).then(res => {
            if (res.ok) {
                showBackendMessage().then();
                return res.json();
            } else {
                throw res.statusText;
            }
        }).then(res => {
            props.deleteAction(props.id);
            message.success("Delete successfully!");
        }).catch(err => message.error(err, 1));
    }

    useEffect(() => load(), []);

    const MemoAntdComment = (props) => {
        return (
            <AntdComment
                author={authorDisplayedName}
                avatar={avatar}
                content={text}
                actions={
                    props.username === author ? [
                        <Button type="link" onClick={() => setHideEditor(!hideEditor)}>
                            <EditOutlined/>Edit
                        </Button>,
                        <Button type="link" onClick={async () => {
                            setHideEditor(true)
                            await onDelete();
                        }}>
                            <DeleteOutlined/>Delete
                        </Button>
                    ] : []
                }
                datetime={timeStamp.toLocaleString()}
            />
        );
    }

    return (
        <li>

        <div hidden={hideEditor}>
            <Card bordered={true}>
                <Form>
                    <Form.Item name="body">
                        <Input.TextArea value={text}/>
                    </Form.Item>
                    <Form.Item>
                        <Button htmlType="submit" type="primary">
                            Submit
                        </Button>
                    </Form.Item>
                </Form>
            </Card>
        </div>
        </li>
    )
};

export default Comment;
