import React, {memo, useEffect, useState} from "react";
import {Card, Input, List, message} from "antd";
import Comment from "./Comment";
import {showBackendMessage, stringArrayEqual} from "../util/Functions";

const Comments = (props) => {
    message.config({
        rtl: false,
    });

    const [commentIdList, setCommentIdList] = useState(props.commentIdList);

    useEffect(() => {
        setCommentIdList(commentIdList => props.commentIdList);
    }, [props]);

    const addNewComment = async (value) => {
        if (!value) {
            message.error("Please input your comment!", 1);
            return;
        }
        await fetch("http://localhost:4000/comment", {
            method: "POST",
            credentials: "include",
            headers: {
                "Content-Type": "application/json",
            },
            body: JSON.stringify({
                articleID: props.articleId,
                text: value,
            }),
        }).then(res => {
            if (res.ok) {
                showBackendMessage();
                return res.json();
            } else {
                throw res.statusText;
            }
        }).then(res => {
            setCommentIdList(commentIdList => res.comments);
            message.success("Comment Successfully!", 1);
        }).catch(err => message.error(err, 1));
    }

    const onDeleteComment = (id) => {
        console.log("id", id);
        console.log("commentIdList", commentIdList);
        setCommentIdList(commentIdList => commentIdList.filter((item) => item !== id));
        console.log("commentIdList", commentIdList);
    }

    useEffect(() => {
        console.log("commentIdList", commentIdList);
    }, [commentIdList])

    return (
        <Card>
            <List
                header={`${commentIdList ? commentIdList.length : 0} replies`}
                itemLayout="horizontal"
                dataSource={commentIdList}
                renderItem={item => <Comment articleId={props.articleId}
                                             id={item}
                                             username={props.username}
                                             deleteAction={onDeleteComment}/>}
            />
            <Input.Search
                allowClear
                onSearch={addNewComment}
                enterButton="Comment"/>
        </Card>
    )
}

const areEqualComments = (prevProps, nextProps) => {
    return stringArrayEqual(prevProps.commentIdList, nextProps.commentIdList);
}

export default memo(Comments, areEqualComments);
