import React from "react";
import {Menu, ConfigProvider} from 'antd';
import {
    HomeOutlined,
    UserOutlined,
    LogoutOutlined
} from '@ant-design/icons';
import {useHistory, useLocation} from "react-router-dom";
import Sticky from 'react-stickynode';
import {handleResponse, showError} from "../util/Functions";
import EventBus from "../util/EventBus";

const MyMediaMenu = () => {
    const location = useLocation();
    const history = useHistory();
    const curPath = location.pathname.slice(1);
    const onSelect = async (info) => {
        if (curPath !== info.key) {
            if (info.key === "logout") {
                await fetch("http://localhost:4000/logout", {
                    method: "PUT",
                    credentials: "include",
                }).then(res => handleResponse(res)).then(() => {
                    history.push({
                        pathname: "/landing",
                    });
                    EventBus.dispatchEvent("clear_all_states");
                }).catch(err => showError(err));
            } else if (info.key === "profile") {
                history.push({
                    pathname: "/profile",
                });
            } else {
                history.push({
                    pathname: "/home",
                })
            }
        }
    }

    return (
        <Sticky innerZ="1">
            <ConfigProvider direction="rtl">
                <Menu
                    theme="dark"
                    mode="horizontal"
                    onSelect={onSelect}
                    selectedKeys={[curPath]}>
                    <Menu.Item key="logout">
                        <LogoutOutlined/>Log Out
                    </Menu.Item>
                    <Menu.Item key="profile">
                        <UserOutlined/>Profile
                    </Menu.Item>
                    <Menu.Item key="home">
                        <HomeOutlined/>Home
                    </Menu.Item>
                </Menu>
            </ConfigProvider>
        </Sticky>
    )
}

export default MyMediaMenu;
