import React, {useEffect, useState, useRef, memo} from "react";
import {Avatar, Button, Card, Image, List, message} from "antd";
import {DeleteOutlined, EditOutlined, MessageOutlined} from "@ant-design/icons";
import SubmitOrEditArticle from "./SubmitOrEditArticle";
import Comments from "./Comments";
import {showBackendMessage, stringArrayEqual} from "../util/Functions";
import Images from "./Images";


const Article = (props) => {
    const [id, setId] = useState(props.id);
    const [author, setAuthor] = useState("");
    const [authorDisplayedName, setAuthorDisplayedName] = useState("");
    const [avatar, setAvatar] = useState("");
    const [hideComments, setHideComments] = useState(true);
    const [hideEditor, setHideEditor] = useState(true);
    const [title, setTitle] = useState("");
    const [text, setText] = useState("");
    const [timestamp, setTimeStamp] = useState("");
    const [imageUrlList, setImageUrlList] = useState([]);
    const [commentIdList, setCommentIdList] = useState([]);

    const load = async () => {
        await fetch("http://localhost:4000/article/" + id, {
            method: "GET",
            credentials: "include",
        }).then(res => {
            if (res.ok) {
                showBackendMessage().then();
                return res.json();
            } else {
                throw res.statusText;
            }
        }).then(async res => {
            setAuthor(res.author);
            setTitle(res.title);
            setText(res.text);
            setTimeStamp(new Date(res.timestamp).toLocaleString());
            setImageUrlList(res.imageUrlList);
            setCommentIdList(res.commentIdList);
            await fetch("http://localhost:4000/profile/" + res.username, {
                method: "GET",
                credentials: "include",
            }).then(res => {
                if (res.ok) {
                    showBackendMessage().then();
                    return res.json();
                } else {
                    throw res.statusText;
                }
            }).then(res => {
                setAuthorDisplayedName(res.displayedName);
                setAvatar(res.avatar);
            });
        }).catch(err => message.error(err, 1));
    };

    useEffect(() => load(), []);

    const onDelete = async () => {
        await fetch("http://localhost:4000/article/" + id, {
            method: "DELETE",
            credentials: "include",
        }).then(res => {
            if (res.ok) {
                showBackendMessage().then();
                return res.json();
            } else {
                throw res.statusText;
            }
        }).then(res => {
            props.deleteAction(props.id);
            message.success("Delete successfully!");
        }).catch(err => message.error(err, 1));
    };

    const onEditArticle = (values) => {
        setTitle(values.title);
        setText(values.text);
        setTimeStamp(values.timestamp);
        setImageUrlList(values.imageUrlList);
    };

    const CommentList = (props) => {
        return (<Comments commentIdList={props.commentIdList} articleId={props.id}/>);
    }

    const areEqualCommentList = (prevProps, nextProps) => {
        return prevProps.commentIdList !== nextProps.commentIdList;
    }

    memo(CommentList, areEqualCommentList);

    const MemoSubmitOrEditArticle = (props) => {
        return (<SubmitOrEditArticle
            action={onEditArticle}
            id={props.id}
            title={props.title}
            text={props.text}
            imageUrlsList={props.imageUrlList}
        />)
    }

    const areEqualMemoSubmitOrEditArticle = (prevProps, nextProps) => {
        return prevProps.id === nextProps.id
            && prevProps.title === nextProps.title
            && prevProps.text === nextProps.text
            && stringArrayEqual(prevProps.imageUrlsList, nextProps.imageUrlsList);
    }

    memo(MemoSubmitOrEditArticle, areEqualMemoSubmitOrEditArticle);

    return (
        <div>
            <List.Item
                key={props.title}
                actions={
                    props.username === author ? [
                        <Button type="link" onClick={() => setHideComments(!hideComments)}>
                            <MessageOutlined/>Comment
                        </Button>,
                        <Button type="link" onClick={() => setHideEditor(!hideEditor)}>
                            <EditOutlined/>Edit
                        </Button>,
                        <Button type="link" onClick={async () => {
                            setHideComments(true);
                            setHideEditor(true);
                            await onDelete();
                        }}>
                            <DeleteOutlined />Delete
                        </Button>,
                    ] : [
                        <Button type="link" onClick={() => setHideComments(!hideComments)}>
                            <MessageOutlined/>Comment
                        </Button>,
                    ]
                }
                extra={
                    imageUrlList.length !== 0 ?
                    <Images images={imageUrlList}/> :
                    <></>
                }
            >
                <List.Item.Meta
                    avatar={<Avatar src={avatar}/>}
                    title={title}
                    description={
                        <div>
                            <span>
                                {authorDisplayedName},
                            </span>
                            <span>
                                {(new Date(timestamp)).toLocaleString()}
                            </span>
                        </div>
                    }/>
                {text}
            </List.Item>
            <div hidden={hideEditor}>
                <Card border="true">
                    <MemoSubmitOrEditArticle
                        id={props.id}
                        title={title}
                        text={text}
                        imageUrlsList={imageUrlList}
                    />
                </Card>
            </div>
            <div hidden={hideComments}>
                <Card border="true">
                    <CommentList commentIdList={props.commentIdList} id={props.id}/>
                </Card>
            </div>
        </div>
    );
}

export default Article;
