import React, {useState} from "react";
import {Input, Button, Form, Card, Divider, message} from "antd";
import {showBackendMessage} from "../util/Functions";
import ImagesWall from "./ImageWall";

const SubmitOrEditArticle = (props) => {
    const [title, setTitle] = useState(props.title);
    const [text, setText] = useState(props.text);
    const [imageUrlsList, setImageUrlsList] = useState(props.imageUrlsList);

    const onFinish = async (values) => {
        if (props.id) {
            await fetch("http://localhost:4000/article", {
                method: "PUT",
                credentials: "include",
                headers: {"Content-Type": "application/json"},
                body: JSON.stringify({
                    title: values.title,
                    text: values.text,
                    images: imageUrlsList,
                }),
            }).then(res => {
                if (res.ok) {
                    showBackendMessage().then();
                    return res.json();
                } else {
                    throw res.statusText;
                }
            }).then(res => {
                message.success("Edit Successfully");
                props.action({
                    id: res._id,
                    title: res.title,
                    text: res.text,
                    timestamp: res.timestamp,
                    images: res.images,
                });
            }).catch(err => message.error(err, 1));
        } else {
            await fetch("http://localhost:4000/article", {
                method: "POST",
                credentials: "include",
                headers: {"Content-Type": "application/json"},
                body: JSON.stringify({
                    title: values.title,
                    text: values.text,
                    images: imageUrlsList,
                }),
            }).then(res => {
                if (res.ok) {
                    showBackendMessage().then();
                    return res.json();
                } else {
                    throw res.statusText;
                }
            }).then(res => {
                message.success("Submit Successfully");
                props.action(res._id);
            }).catch(err => message.error(err, 1));
        }
    }

    return (
        <Card bordered={true}>
            <Divider orientation="left">{
                props.id ? "Edit Post" : "Add New Post"
            }</Divider>
            <Form onFinish={onFinish} initialValue={{
                "title": title,
                "text": text,
            }}>
                <Form.Item name="title">
                    <Input placeholder="Title"/>
                </Form.Item>
                <Form.Item name="text">
                    <Input.TextArea/>
                </Form.Item>
                <Form.Item>
                    <ImagesWall
                        imageUrlsList={imageUrlsList}
                        action={newImageUrlsList => setImageUrlsList(newImageUrlsList)} />
                </Form.Item>
                <Form.Item>
                    <Button style={{width: "50%"}} htmlType="submit" type="primary">
                        Submit
                    </Button>
                    <Button style={{width: "50%"}} htmlType="reset" type="default">
                        Clear
                    </Button>
                </Form.Item>
            </Form>
        </Card>
    )
}

export default SubmitOrEditArticle;
