import React, {useEffect, useState, useRef} from "react";
import {Card, Divider, Input, List, message} from "antd";
import Article from "./Article";
import SubmitOrEditArticle from "./SubmitOrEditArticle";
import {showBackendMessage} from "../util/Functions";
import EventBus from "../util/EventBus";

const Articles = () => {
    message.config({
        rtl: false,
    });

    const [selectedArticleIds, setSelectedArticleIds] = useState([]);
    const [username, setUsername] = useState("");

    const load = async () => {
        await fetch("http://localhost:4000/search/article", {
            method: "POST",
            credentials: "include",
        }).then(res => {
            if (res.ok) {
                showBackendMessage().then();
                return res.json();
            } else {
                throw res.statusText;
            }
        }).then(res => {
            setUsername(res.username);
            setSelectedArticleIds(selectedArticleIds => res.ids);
        }).catch(err => message.error(err, 1));
    }

    useEffect(() => load(), []);
    EventBus.addEventListener("reload_articles", load);
    const onSearch = async (value) => {
        await fetch("http://localhost:4000/search/article", {
            method: "POST",
            credentials: "include",
            headers: {
                "Content-Type": "application/json"
            },
            body: JSON.stringify({
                keyWord: value,
            })
        }).then(res => {
            if (res.ok) {
                showBackendMessage().then();
                return res.json();
            } else {
                throw res.statusText;
            }
        }).then(res => {
            setSelectedArticleIds(selectedArticleIds => res.ids);
        }).catch(err => message.error(err, 1));
    }

    const onSubmitArticle = (id) => {
        let newOne = [id].concat(selectedArticleIds);
        setSelectedArticleIds([...newOne]);
    }

    const deleteArticle = (id) => {
        setSelectedArticleIds(selectedArticleIds => selectedArticleIds.filter((item) => item !== id));
    }

    useEffect(() => {
        console.log("selectedArticleIds", selectedArticleIds);
    }, [selectedArticleIds]);

    const ArticleList = (props) => {
        return (
            <List
                itemLayout="vertical"
                size="large"
                dataSource={props.selectedArticleIds}
                renderItem={item => <Article id={item} username={username} deleteAction={deleteArticle}/>}
                pagination={{
                    position: "top",
                    pageSize: 5
                }}
            />
        );
    }

    const areEqualArticleList = (prevProps, nextProps) => { // TODO: Algorithm Optimization
        for (let id of prevProps.selectedArticleIds) {
            let index = nextProps.selectedArticleIds.indexOf(id);
            if (index === -1) return false;
        }
        for (let id of prevProps.selectedArticleIds) {
            let index = nextProps.selectedArticleIds.indexOf(id);
            if (index === -1) return false;
        }
        return true;
    }

    React.memo(ArticleList, areEqualArticleList);

    return (
        <>
            <SubmitOrEditArticle action={onSubmitArticle}/>
            <Card bordered={true}>
                <Input.Search
                    allowClear
                    enterButton="Search"
                    size="large"
                    onSearch={onSearch}
                />
                <Divider orientation="left">Posts</Divider>
                <ArticleList selectedArticleIds={selectedArticleIds} />
            </Card>
        </>
    )
}

export default Articles;
