import React, {useState} from "react";
import {Button, Card, Divider, Input, List, Comment} from "antd";
import {connect} from "react-redux";
import {EditOutlined, MessageOutlined} from "@ant-design/icons";
import Avatar from "antd/es/avatar/avatar";
import {search} from "../actions";

// TODO: Real Comments

const Posts = (props) => {

    const onSearch = (value) => {
        props.search({
            searchWord: value
        });
    }

    const Post = (propsPost) => {
        propsPost = propsPost.propsPost; // ???
        const [showComments, setShowComments] = useState(true);

        return (
            <div>
                <List.Item
                    key={propsPost.title}
                    actions={
                        propsPost.userName === props.userName ? [
                            <Button type="link">
                                <EditOutlined/>Edit
                            </Button>,
                            <Button type="link" onClick={() => setShowComments(!showComments)}>
                                <MessageOutlined/>Comment
                            </Button>,
                        ] : [
                            <Button type="link" onClick={() => setShowComments(!showComments)}>
                                <MessageOutlined/>Comment
                            </Button>,
                        ]
                    }
                    extra={
                        propsPost.image ?
                            <img src={propsPost.image} alt={propsPost.title}/> :
                            <></>
                    }
                >
                    <List.Item.Meta
                        avatar={<Avatar src={propsPost.avatar}/>}
                        title={propsPost.title}
                        description={
                            <div>
                                <span>
                                    {propsPost.userName}
                                </span>
                                , <span>
                                    {(new Date(propsPost.timestamp)).toLocaleString()}
                                </span>
                            </div>
                        }/>
                    {propsPost.body}
                </List.Item>
                <div hidden={showComments}>
                    <Card>
                        <List
                            header={`${propsPost.comments.length} replies`}
                            itemLayout="horizontal"
                            dataSource={propsPost.comments}
                            renderItem={item => (
                                <li>
                                    <Comment
                                        author={item.author}
                                        avatar={item.avatar}
                                        content={item.content}
                                    />
                                </li>
                            )}
                        />
                        <Input.Search
                            allowClear
                            enterButton="Comment"/>
                    </Card>
                    <Divider/>
                </div>

            </div>
        );
    }

    return (
        <Card bordered={true}>
            <Input.Search
                defaultValue={props.searchWord}
                allowClear
                enterButton="Search"
                size="large"
                onSearch={onSearch}
            />
            <Divider orientation="left">Posts</Divider>
            <List
                itemLayout="vertical"
                size="large"
                dataSource={props.selectedPosts}
                renderItem={item => <Post propsPost={item}/>}
            />
        </Card>
    )
}

const mapStateToProps = (state) => {
    return {
        selectedPosts: state.selectedPosts,
        userName: state.userName,
        searchWord: state.searchWord,
    }
};

const mapDispatchToProps = (dispatch) => {
    return {
        search: (values) => dispatch(search(values)),
    }
}

export default connect(mapStateToProps, mapDispatchToProps)(Posts);
