import React, {useEffect, useState, useRef} from "react";
import {Card, Divider, Input, List, message} from "antd";
import Article from "./Article";
import SubmitOrEditArticle from "./SubmitOrEditArticle";
import {showBackendMessage} from "../util/Functions";
import EventBus from "../util/EventBus";

const Articles = () => {
    message.config({
        rtl: false,
    });

    const [selectedArticleIds, setSelectedArticleIds] = useState([]);
    const [username, setUsername] = useState("");

    const load = async () => {
        await fetch("http://localhost:4000/search/article", {
            method: "POST",
            credentials: "include",
        }).then(res => {
            if (res.ok) {
                showBackendMessage().then();
                return res.json();
            } else {
                throw res.statusText;
            }
        }).then(res => {
            setUsername(res.username);
            setSelectedArticleIdsString("[" + res.ids.toString() + "]");
            selectedArticleIdsRef.current = window.Function('"use strict";return ' + selectedArticleIdsString)();
            console.log("selectedArticleIdsRef.current", selectedArticleIdsRef.current);
        }).catch(err => message.error(err, 1));
    }

    useEffect(() => load(), []);
    EventBus.addEventListener("reload_articles", load);
    EventBus.addEventListener("reload_all", load);
    EventBus.addEventListener("clear_all_states", () => {
        setSelectedArticleIdsString("[]");
    })


    const onSearch = async (value) => {
        await fetch("http://localhost:4000/search/article", {
            method: "POST",
            credentials: "include",
            headers: {
                "Content-Type": "application/json"
            },
            body: JSON.stringify({
                keyWord: value,
            })
        }).then(res => {
            if (res.ok) {
                showBackendMessage().then();
                return res.json();
            } else {
                throw res.statusText;
            }
        }).then(res => {
            setSelectedArticleIdsString("[" + res.ids.toString() + "]");
        }).catch(err => message.error(err, 1));
    }

    const onSubmitArticle = async (values) => {
        setSelectedArticleIdsString("[" + values.ids.toString() + "]");
    }

    return (
        <>
            <SubmitOrEditArticle action={onSubmitArticle}/>
            <Card bordered={true}>
                <Input.Search
                    allowClear
                    enterButton="Search"
                    size="large"
                    onSearch={onSearch}
                />
                <Divider orientation="left">Posts</Divider>
                <List
                    itemLayout="vertical"
                    size="large"
                    dataSource={selectedArticleIdsRef.current}
                    renderItem={item => <Article id={item} username={username}/>}
                />
            </Card>
        </>
    )
}

export default Articles;
