import React, {memo, useEffect, useState} from "react";
import {Button, Card, Form, Input, Comment as AntdComment, message} from "antd";
import {DeleteOutlined, EditOutlined} from "@ant-design/icons";
import {showBackendMessage} from "../util/Functions";

const Comment = (props) => {
    message.config({
        rtl: false,
    });

    const [form] = Form.useForm();
    const [hideEditor, setHideEditor] = useState(true);
    const [id, setId] = useState(props.id);
    const [username, setUsername] = useState("");
    const [author, setAuthor] = useState("");
    const [authorDisplayedName, setAuthorDisplayedName] = useState("");
    const [avatar, setAvatar] = useState("");
    const [text, setText] = useState("");
    const [timeStamp, setTimeStamp] = useState(0);

    const load = async () => {
        await fetch("http://localhost:4000/comment/" + id, {
            method: "GET",
            credentials: "include"
        }).then(res => {
            if (res.ok) {
                showBackendMessage();
                return res.json();
            } else {
                throw res.statusText;
            }
        }).then(async res => {
            setAuthor(res.author);
            setUsername(res.username);
            setText(res.text);
            form.setFieldsValue({
                "text": text,
            });
            setTimeStamp(res.timestamp);
            await fetch("http://localhost:4000/profile/" + author, {
                method: "GET",
                credentials: "include",
            }).then(res => {
                if (res.ok) {
                    showBackendMessage();
                    return res.json();
                } else {
                    throw res.statusText;
                }
            }).then(res => {
                setAuthorDisplayedName(res.displayedName);
                setAvatar(res.avatar);
            }).catch(err => message.error(err, 1));
        }).catch(err => message.error(err, 1));
    }

    const onDelete = async () => {
        await fetch("http://localhost:4000/comment/" + id, {
            method: "DELETE",
            credentials: "include",
            headers: {
                "Content-Type": "application/json",
            },
            body: JSON.stringify({
                articleId: props.articleId,
            })
        }).then(res => {
            if (res.ok) {
                showBackendMessage();
                return res.json();
            } else {
                throw res.statusText;
            }
        }).then(() => {
            props.deleteAction(props.id);
            message.success("Delete successfully!");
        }).catch(err => message.error(err, 1));
    };

    const onSubmit = async () => {
        await fetch("http://localhost:4000/comment/" + id, {
            method: "PUT",
            credentials: "include",
            headers: {
                "Content-Type": "application/json",
            },
            body: JSON.stringify({
                text: text,
            }),
        }).then(res => {
            if (res.ok) {
                showBackendMessage();
                return res.json();
            } else {
                throw res.statusText;
            }
        }).then(res => {
            console.log("res.text", res.text);
            setText(res.text);
            message.success("Update successfully!");
        }).catch(err => message.error(err, 1));
    }

    useEffect(() => load(), []);

    return (
        <li>
        <AntdComment
            author={authorDisplayedName}
            avatar={avatar}
            content={text}
            actions={
                username === author ? [
                    <Button type="link" onClick={() => setHideEditor(!hideEditor)}>
                        <EditOutlined/>Edit
                    </Button>,
                    <Button type="link" onClick={async () => {
                        setHideEditor(true)
                        await onDelete();
                    }}>
                        <DeleteOutlined/>Delete
                    </Button>
                ] : []
            }
            datetime={(new Date(timeStamp)).toLocaleString()}
        />
        <div hidden={hideEditor}>
            <Card bordered={true}>
                <Form form={form}>
                    <Form.Item name="body">
                        <Input.TextArea/>
                    </Form.Item>
                    <Form.Item>
                        <Button htmlType="submit" type="primary" onClick={onSubmit}>
                            Submit
                        </Button>
                    </Form.Item>
                </Form>
            </Card>
        </div>
        </li>
    )
};

export default Comment;
