import React, {useEffect, useState} from "react";
import {handleResponse, showError} from "../util/Functions";
import EventBus from "../util/EventBus";
import {Button, Form} from "antd";
import {formItemLayout, tailFormItemLayout} from "../util/Layout";
import {GoogleOutlined} from "@ant-design/icons";

const GoogleBinding = () => {
    const [googleEmail, setGoogleEmail] = useState("");

    const load = async () => {
        await fetch("http://localhost:4000/google_email", {
            method: "GET",
            credentials: "include",
        }).then(res => handleResponse(res)).then(res => {
            setGoogleEmail(res.google_email);
        }).catch(err => showError(err));
    }

    useEffect(() => load, []);
    EventBus.addEventListener("reload_google_binding", load);
    EventBus.addEventListener("reload_all", load);
    EventBus.addEventListener("clear_all_states", () => {
        setGoogleEmail("");
    });

    const onFinish = async () => {
        window.location.href = "http://localhost:3000/auth/google";
    }

    return (
        <Form {...formItemLayout}
              name="userGoogleBinding"
              autoComplete="off"
              onFinish={onFinish}
        >
            <Form.Item name="googleEmail"
                       label="Google Account"
            >
                <span className="ant-form-text">
                    {googleEmail}
                </span>
            </Form.Item>

            <Form.Item {...tailFormItemLayout}>
                <Button type="primary" htmlType="submit">
                    {<GoogleOutlined/>}
                </Button>
            </Form.Item>
        </Form>
    )
}

export default GoogleBinding;
