import React from "react";
import {Input, Button, Form, Upload, Card, Divider} from "antd";
import {UploadOutlined} from "@ant-design/icons";
import {connect} from "react-redux";
import {post} from "../actions";


// TODO: 1. Real image uploader
//       2. Style Optimization
const SubmitNewPost = (props) => {
    const onFinish = (values) => {
        console.log(values);
        props.post({
            title: values.title,
            body: values.body,
        })
    }

    return (
        <Card bordered={true}>
            <Divider orientation="left">New Post</Divider>
            <Form onFinish={onFinish}>
                <Form.Item name="title">
                    <Input placeholder="Title"/>
                </Form.Item>
                <Form.Item name="body">
                    <Input.TextArea/>
                </Form.Item>
                <Form.Item>
                    <Button style={{width: "50%"}} htmlType="submit" type="primary">
                        Submit
                    </Button>
                    <Button style={{width: "50%"}} htmlType="reset" type="default">
                        Clear
                    </Button>
                </Form.Item>
                <Form.Item>
                    <Upload>
                        <Button type="dashed">
                            <UploadOutlined/>Upload Images
                        </Button>
                    </Upload>
                </Form.Item>
            </Form>
        </Card>
    )
}

const mapStateToProps = (state) => {
    return {}
};

const mapDispatchToProps = (dispatch) => {
    return {
        post: (values) => dispatch(post(values))
    }
}

export default connect(mapStateToProps, mapDispatchToProps)(SubmitNewPost);
