import React from "react";
import {message, Button, Form, Input, Space} from "antd";
import {formItemLayout, tailFormItemLayout} from '../util/Layout';
import {GoogleOutlined} from "@ant-design/icons";
import {useHistory} from "react-router-dom";
import {showBackendMessage} from "../util/Functions";
import EventBus from "../util/EventBus";


const LogInForm = () => {
    message.config({
        rtl: false,
    });

    const history = useHistory();

    const onFinish = async (values) => {
        await fetch("http://localhost:4000/login", {
            method: "POST",
            credentials: "include",
            headers: {"Content-Type": "application/json"},
            body: JSON.stringify({
                username: values.username,
                password: values.password,
            }),
        }).then(res => {
            if (res.ok) {
                showBackendMessage().then();
                return res.json();
            } else {
                throw res.statusText;
            }
        }).then(() => {
            EventBus.dispatchEvent("reload_all");
            history.push({
                pathname: "/home"
            });
            message.success("Login Successfully!");
        }).catch(err => message.error(err, 1));
    };

    const googleLogin =  () => {
        window.location.href = "https://localhost:4000/auth/google/login";
    }

    return (
        <Form {...formItemLayout}
              name="logIn"
              autoComplete="off"
              layout="horizontal"
              onFinish={onFinish}
        >

            <Form.Item name="username"
                       label="Username"
                       rules={[
                           {
                               required: true,
                               message: 'Please input your username!',
                           }
                       ]}
            >
                <Input/>
            </Form.Item>

            <Form.Item name="password"
                       label="Password"
                       rules={[
                           {
                               required: true,
                               message: 'Please input your password!',
                           }
                       ]}
            >
                <Input.Password/>
            </Form.Item>

            <Form.Item {...tailFormItemLayout}>
                <Space size={8}>
                    <Button type="primary" htmlType="submit">
                        Log in
                    </Button>
                    <Button type="default" htmlType="button" onClick={googleLogin}>
                        Log in with <GoogleOutlined />
                    </Button>
                </Space>
            </Form.Item>
        </Form>
    );
}

export default LogInForm;
