import React, {useEffect, useState, useRef, memo} from "react";
import {Avatar, Button, Card, Image, List, message} from "antd";
import {DeleteOutlined, EditOutlined, MessageOutlined} from "@ant-design/icons";
import SubmitOrEditArticle from "./SubmitOrEditArticle";
import Comments from "./Comments";
import {showBackendMessage} from "../util/Functions";
import Images from "./Images";


const Article = (props) => {
    const [id, setId] = useState(props.id);
    const [author, setAuthor] = useState("");
    const [authorDisplayedName, setAuthorDisplayedName] = useState("");
    const [avatar, setAvatar] = useState("");
    const [hideComments, setHideComments] = useState(true);
    const [hideEditor, setHideEditor] = useState(true);
    const [hideArticle, setHideArticle] = useState(false);
    const [title, setTitle] = useState("");
    const [text, setText] = useState("");
    const [timestamp, setTimeStamp] = useState("");
    const [imageUrlList, setImageUrlList] = useState([]);
    const [commentIdList, setCommentIdList] = useState([]);

    const imageUrlListRef = useRef();
    const commentIdListRef = useRef();

    useEffect(() => {
        imageUrlListRef.current = imageUrlList;
        // console.log("imageUrlList", imageUrlList);
    }, [imageUrlList]);

    useEffect(() => {
        commentIdListRef.current = commentIdList;
        // console.log("commentIdList", commentIdList);
    }, [commentIdList]);

    const load = async () => {
        await fetch("http://localhost:4000/article/" + id, {
            method: "GET",
            credentials: "include",
        }).then(res => {
            if (res.ok) {
                showBackendMessage().then();
                return res.json();
            } else {
                throw res.statusText;
            }
        }).then(async res => {
            setAuthor(res.author);
            setTitle(res.title);
            setText(res.text);
            setTimeStamp(new Date(res.timestamp).toLocaleString());
            setImageUrlList([...res.imageUrlList]);
            setCommentIdList([...res.commentIdList]);
            console.log(title, id, commentIdList, commentIdListRef, res.commentIdList, );
            await fetch("http://localhost:4000/profile/" + res.username, {
                method: "GET",
                credentials: "include",
            }).then(res => {
                if (res.ok) {
                    showBackendMessage().then();
                    return res.json();
                } else {
                    throw res.statusText;
                }
            }).then(res => {
                setAuthorDisplayedName(res.displayedName);
                setAvatar(res.avatar);
            });
        }).catch(err => message.error(err, 1));
    };

    useEffect(() => load(), []);

    const onDelete = async () => {
        await fetch("http://localhost:4000/article/" + id, {
            method: "DELETE",
            credentials: "include",
        }).then(res => {
            if (res.ok) {
                showBackendMessage().then();
                return res.json();
            } else {
                throw res.statusText;
            }
        }).then(res => {
            setHideArticle(true);
            message.success("Delete successfully!");
        }).catch(err => message.error(err, 1));
    };

    const onEditArticle = (values) => {
        setTitle(values.title);
        setText(values.text);
        setTimeStamp(values.timestamp);
        setImageUrlList([...values.imageUrlList]);
    };

    return (
        <div hidden={hideArticle}>
            <List.Item
                key={props.title}
                actions={
                    props.username === author ? [
                        <Button type="link" onClick={() => setHideComments(!hideComments)}>
                            <MessageOutlined/>Comment
                        </Button>,
                        <Button type="link" onClick={() => setHideEditor(!hideEditor)}>
                            <EditOutlined/>Edit
                        </Button>,
                        <Button type="link" onClick={async () => {
                            setHideComments(true);
                            setHideEditor(true);
                            await onDelete();
                        }}>
                            <DeleteOutlined />Delete
                        </Button>,
                    ] : [
                        <Button type="link" onClick={() => setHideComments(!hideComments)}>
                            <MessageOutlined/>Comment
                        </Button>,
                    ]
                }
                extra={
                    imageUrlList.length !== 0 ?
                    <Images images={imageUrlList}/> :
                    <></>
                }
            >
                <List.Item.Meta
                    avatar={<Avatar src={avatar}/>}
                    title={title}
                    description={
                        <div>
                            <span>
                                {authorDisplayedName},
                            </span>
                            <span>
                                {(new Date(timestamp)).toLocaleString()}
                            </span>
                        </div>
                    }/>
                {text}
            </List.Item>
            <div hidden={hideEditor}>
                <Card border="true">
                    <SubmitOrEditArticle action={onEditArticle}/>
                </Card>
            </div>
            <div hidden={hideComments}>
                <Card border="true">
                    <Comments commentIdList={commentIdList}
                              articleId={id}/>
                </Card>
            </div>
        </div>
    );
}

const areEqualArticle = (prevProps, nextProps) => {
    return false;
}

export default memo(Article, areEqualArticle);
