import React, {useEffect, useState, useRef} from "react";
import {Card, Divider, Input, List, message} from "antd";
import Article from "./Article";
import SubmitOrEditArticle from "./SubmitOrEditArticle";
import {showBackendMessage} from "../util/Functions";
import EventBus from "../util/EventBus";

const Articles = () => {
    message.config({
        rtl: false,
    });

    const [selectedArticleIds, setSelectedArticleIds] = useState([]);
    const [username, setUsername] = useState("");

    const load = async () => {
        await fetch("http://localhost:4000/search/article", {
            method: "POST",
            credentials: "include",
        }).then(res => {
            if (res.ok) {
                showBackendMessage().then();
                return res.json();
            } else {
                throw res.statusText;
            }
        }).then(res => {
            setUsername(res.username);
            setSelectedArticleIds(res.ids);
        }).catch(err => message.error(err, 1));
    }

    useEffect(() => load(), []);
    EventBus.addEventListener("reload_articles", load);
    const onSearch = async (value) => {
        await fetch("http://localhost:4000/search/article", {
            method: "POST",
            credentials: "include",
            headers: {
                "Content-Type": "application/json"
            },
            body: JSON.stringify({
                keyWord: value,
            })
        }).then(res => {
            if (res.ok) {
                showBackendMessage().then();
                return res.json();
            } else {
                throw res.statusText;
            }
        }).then(res => {
            setSelectedArticleIds(res.ids);
        }).catch(err => message.error(err, 1));
    }

    const onSubmitArticle = (values) => {
        let newOne = [values.id].concat(selectedArticleIds);
        setSelectedArticleIds([...newOne]);
    }

    useEffect(() => {
        console.log("selectedArticleIds", selectedArticleIds);
    }, [selectedArticleIds]);

    const ArticlesList = (props) => {
        <List
            itemLayout="vertical"
            size="large"
            dataSource={selectedArticleIds}
            renderItem={item => <Article id={item} username={username}/>}
            pagination={{
                position: "top",
                pageSize: 5
            }}
        />
    }

    return (
        <>
            <SubmitOrEditArticle action={onSubmitArticle}/>
            <Card bordered={true}>
                <Input.Search
                    allowClear
                    enterButton="Search"
                    size="large"
                    onSearch={onSearch}
                />
                <Divider orientation="left">Posts</Divider>

            </Card>
        </>
    )
}

export default Articles;
