import React, {useEffect, useState} from "react";
import {Card, List, Input, Divider, message} from "antd";
import {showBackendMessage} from "../util/Functions";
import EventBus from "../util/EventBus";
import Following from "./Following";
import {CloseCircleOutlined, PlusCircleOutlined} from "@ant-design/icons";

const Followings = () => {

    message.config({
        rtl: false,
    });
    const [followings, setFollowings] = useState([]);
    const [searchUserList, setSearchUserList] = useState([]);
    const [showSearchUserList, setShowSearchUserList] = useState(false);

    const load = async () => {
        await fetch("http://localhost:4000/following", {
            method: "GET",
            credentials: "include",
        }).then(res => {
            if (res.ok) {
                showBackendMessage();
                return res.json();
            } else {
                throw res.statusText;
            }
        }).then(res => {
            setFollowings(followings => res.followings.reverse());
        }).catch(err => message.error(err, 1));
    };

    useEffect(() => load(), []);

    const showSearchResult = async (value) => {
        if (value === "") {
            setShowSearchUserList(false);
            setSearchUserList([]);
        } else {
            setShowSearchUserList(true);
            await fetch("http://localhost:4000/following/search", {
                method: "POST",
                credentials: "include",
                headers: {
                    "Content-Type": "application/json",
                },
                body: JSON.stringify({
                    keyWord: value,
                }),
            }).then(res => {
                if (res.ok) {
                    showBackendMessage();
                    return res.json();
                } else {
                    throw res.statusText;
                }
            }).then(res => {
                setFollowings(followings => res.followings);
                let index = searchUserList.indexOf(value);
                setSearchUserList(searchUserList => searchUserList.filter((item => item !== index)));
            }).catch(err => message.error(err, 1));
        }
    }

    const addNewFollowing = (value) => async () => {
        await fetch("http://localhost:4000/following/" + value, {
            method: "PUT",
            credentials: "include",
        }).then(res => {
            if (res.ok) {
                showBackendMessage();
                return res.json();
            } else {
                throw res.statusText;
            }
        }).then(res => {
            message.success("Added a new following user");
            setFollowings(res.followings);
            let newSearchUserList = searchUserList;
            let index = newSearchUserList.indexOf(value);
            newSearchUserList.splice(index, 1);
            setSearchUserList(newSearchUserList);
            EventBus.dispatchEvent("reload_articles");
        }).catch(err => message.error(err, 1));
    };

    const deleteFollowing = (value) => async () => {
        await fetch("http://localhost:4000/following/" + value, {
            method: "DELETE",
            credentials: "include",
        }).then(res => {
            if (res.ok) {
                showBackendMessage();
                return res.json();
            } else {
                throw res.statusText;
            }
        }).then(() => {
            message.success("Delete a followed user");
            setShowSearchUserList(false);
        }).catch(err => message.error(err, 1));
    };

    return (
        <Card bordered={true}>
            <Divider orientation="left">My followings</Divider>
            <Input.Search
                allowClear
                enterButton="Add Following"
                size="large"
                onSearch={showSearchResult}
            />
            <div hidden={!showSearchUserList}>
                <Card bordered={true}>
                    <List
                        dataSource={searchUserList}
                        renderItem={(item) => <Following
                            username={item.username}
                            action={deleteFollowing(item.username)}
                            actionSymbol={<CloseCircleOutlined />}
                        />}
                        pagination={{
                            position: "top",
                            pageSize: 5
                        }}
                    />
                </Card>
            </div>
            <List
                dataSource={followings}
                renderItem={(item) => <Following
                    username={item.username}
                    action={addNewFollowing(item.username)}
                    actionSymbol={<PlusCircleOutlined />}
                />}
            />
        </Card>
    )
}

export default Followings;
